import React, { useState } from 'react'
import styles from './../../styles/MintRight.module.css'
import { walletAddress } from './../../atoms/walletAddress'
import { useRecoilState } from 'recoil'
import Web3 from 'web3'

const MintRight = () => {
	const [amount, setAmount] = useState(1)
	const [address, setAddress] = useRecoilState(walletAddress)

	async function ConnectWallet() {
		try {
			if (window.ethereum) {
				let web3 = new Web3(window.ethereum)
				await window.ethereum.send('eth_requestAccounts')
				let accounts = await web3.eth.getAccounts()
				const account = accounts[0]
				console.log(account)
				// setAddress(account)
			}
		} catch (error) {
			console.log(`Something went wrong`)
		}
	}

	return (
		<div className={styles.container}>
			<div className={styles.connectButton}>
				{address ? (
					<>
						<p
							style={
								address && {
									borderColor: 'green',
									color: 'green',
									cursor: 'auto',
									userSelect: 'none',
								}
							}
						>
							Connected
						</p>
					</>
				) : (
					<p
						style={{
							userSelect: 'none',
						}}
						onClick={ConnectWallet}
					>
						Connect Wallet
					</p>
				)}
			</div>
			<div className={styles.btnContainer}>
				<div>
					<p className={styles.amount}>
						Amount <span>(max 50)</span>
					</p>
					<div className={styles.buttons}>
						<div>
							<span>{amount}</span>
						</div>
						<div>
							<span
								style={{
									cursor: 'pointer',
									userSelect: 'none',
								}}
								onClick={() => setAmount(amount + 1)}
							>
								+
							</span>
						</div>
						<div>
							<span
								style={{
									cursor: 'pointer',
									userSelect: 'none',
								}}
								onClick={() =>
									amount > 1 ? setAmount(amount - 1) : setAmount(amount)
								}
							>
								-
							</span>
						</div>
					</div>
				</div>
				<div className={styles.buttonClaim}>
					<p>Claim</p>
				</div>
			</div>
		</div>
	)
}

export default MintRight
